import React from "react";
import Userlist from "../userList/userList";

import {useEffect,useState} from 'react'

const Assignment3 = () => {

    const [userList,UpdateUserList] = useState([])
    const [count,updateCount] = useState(0)

    useEffect(()=>{
        fetch('https://jsonplaceholder.typicode.com/users')
        .then((data)=>data.json())
        .then((res)=>UpdateUserList(res))
    },[])


    function countUpdate() {
        updateCount(count+1)
    }


    return (
        <div>
            <h1 className="text-center bg-black text-white">Assignment 3</h1>
            <h1 className="text-center p-3 font-bold">User Count : {count}</h1>
            <Userlist usersList={userList} countUpdate={countUpdate}/>
            <h1 className="text-center bg-black text-white">End of Assignment 3</h1>
        </div>
    )
}


export default Assignment3;
