import {React,useContext,useState} from 'react'
import { TaskContext } from '../../contexts/TaskContext'
import { CompletedContext } from '../../contexts/CompletedContext'

import TaskComp from '../Task_c/TaskComp'


const TaskAdder = () => {

    let [taskList,addTask] = useContext(TaskContext)

    let [localTask,updateLocalTask] = useState("")

    let [completedTasks] = useContext(CompletedContext)

    function handleLocalTaskUpdate(e){

      updateLocalTask(e.target.value)

    }

    function handleTaskSubmission(){

      if(localTask.length === 0) return

      addTask([...taskList,localTask])

    }




  return (
    <div className='flex flex-col lg:w-[50%] lg:h-screen items-center justify-start mt-20 '>

        <h1 className='font-bold text-8xl mb-9'>Add Your task</h1>

        <input id='addtask' type='text' placeholder='Enter your task' className='p-3 w-[50%] outline-none rounded-full border-solid border-black border-2 shadow-cyan-950 shadow-lg' onChange={handleLocalTaskUpdate} ></input>

        <button type='submit' className='mt-8 border-solid border-black border-2 font-bold text-4xl mb-9 rounded-full p-3 hover:bg-red-500 hover:text-white' onClick={handleTaskSubmission}>submit</button>

        <div className='bg-green-100 w-full h-fit'>
            {completedTasks && completedTasks.map((task,index) => <TaskComp key={index} task={task}/>)}
        </div>

    </div>
  )
}

export default TaskAdder
