import React from 'react'

import {useContext} from 'react'
import { TaskContext } from '../../contexts/TaskContext'




const Task = ({id,task}) => {

    let [taskList,addtask] = useContext(TaskContext)

    function removeTask(){
        let taskCopy = [...taskList]

        taskCopy.splice(id,1)

        addtask(taskCopy)
    }

  return (
    <div className='flex flex-row items-center border-solid border-2 border-black gap-4 p-3 cursor-move text-lg' draggable={true}>
        <span>::</span>
        <span className='text-4xl'>{task}</span>
        <button className='bg-red-500 font-bold border-solid border-2 border-black p-3' onClick={removeTask}>Delete</button>
        <button className='bg-green-500 font-bold border-solid border-2 border-black p-3'>Done</button>
    </div>
  )
}

export default Task
