import {React,useContext,useState} from 'react'
import { TaskContext } from '../../contexts/TaskContext'


const TaskAdder = () => {

    let [taskList,addTask] = useContext(TaskContext)

    let [localTask,updateLocalTask] = useState("")



  return (
    <div className='flex flex-col lg:w-[50%] lg:h-screen items-center justify-start mt-40 '>

        <h1 className='font-bold text-8xl mb-9'>Add Your task</h1>

        <input id='addtask' type='text' placeholder='Enter your task' className='p-3 w-[50%] outline-none rounded-full border-solid border-black border-2 shadow-cyan-950 shadow-lg' onChange={ (e) => updateLocalTask(e.target.value) }></input>

    </div>
  )
}

export default TaskAdder
